import React, { useState, useEffect } from 'react'
import './clientServiceDataLog.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'

import {ubuntuIP} from '../../Components/constantVariable'

let ClientServiceDataLog = () => {

  let initialState = {
    s_entity_id: '',
    s_entity_id_and_name: '',
  }



  let [clientServiceDataLogRegDetails, setclientServiceDataLogRegDetails] = useState({
    s_entity_id: '',
    s_entity_id_and_name: '',
  })


  let handleNameChange = (e, s_entity_id1) => {
    let { name, value } = e.target
    if (name === 's_entity_id_and_name') {
      setclientServiceDataLogRegDetails(prevData => ({
        ...prevData,
        s_entity_id_and_name: value,
        s_entity_id: s_entity_id1
      }))
    }
  }
  let [entityNames, setEntityNames] = useState({ data: [] })
  useEffect(() => {
    fetch(`${ubuntuIP}/api/v0/getAllEntityNameList`)
      .then(response => response.json())
      .then(data => {
        setEntityNames({ data })
      })
      .catch(error => {
        console.error('Error: ', error)
      })
  }, [])


  let resetForm = () => {
    setclientServiceDataLogRegDetails(initialState)
  }

  let refreshPage = () => {
    window.location.reload()
  }

  let handleSubmit = e => {
    e.preventDefault()
    fetch(`${ubuntuIP}/api/v0/getClientDeviceDetails`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(clientServiceDataLogRegDetails)
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success in clientServiceDataLog Creation Form:', data)
        alert('clientServiceDataLog created successfully!')
      })
      .catch(error => {
        console.error('Error:', error)
        alert('Error! Please try again.')
      })
    console.log('clientServiceDataLog creation form submitted:', clientServiceDataLogRegDetails)
  }

  return (
    <div>
      <div className='wrapper'>
        <div className='container'>
          <h2>Client Service Data Log</h2>
          <div className='clientServiceDataLogRegistrationForm'>
            <form onSubmit={handleSubmit}>
              <div className='form-group'>
                <label
                  htmlFor='s_entity_id_and_name'
                  className={`required-label ${
                    clientServiceDataLogRegDetails.s_entity_id_and_name ? 'required' : ''
                  }`}
                >
                  Entity:
                </label>
                <select
                  className='form-select'
                  id='s_entity_id_and_name'
                  name='s_entity_id_and_name'
                  required
                  value={clientServiceDataLogRegDetails.s_entity_id_and_name}
                  onChange={e => {
                    let selectedEntity = entityNames.data.data.find(
                      entity => entity.s_entity_name === e.target.value
                    )
                    handleNameChange(e, selectedEntity?.s_entity_id)
                  }}
                >
                  <option value=''>Select</option>
                  {entityNames.data && Array.isArray(entityNames.data.data) ? (
                    entityNames.data.data.map(entity => (
                      <option
                        key={entity.s_entity_id}
                        value={entity.s_entity_name}
                      >
                        {entity.s_entity_name}
                      </option>
                    ))
                  ) : (
                    <option value=''>
                      {entityNames.data && entityNames.data.message
                        ? entityNames.data.message
                        : 'No entities available'}
                    </option>
                  )}
                </select>
              </div>
              <div class='form-buttons'>
                <button type='submit'>Save</button>
                <button
                  type='button'
                  className='cancel-button'
                  onClick={() => {
                    resetForm()
                  }}
                >
                  Cancel
                </button>
                <button
                  type='button'
                  className='refresh-button'
                  onClick={refreshPage}
                >
                  Refresh
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}
export default ClientServiceDataLog
