import React, { useState, useEffect } from 'react'
import './deviceDetails.css'

import { ubuntuIP } from '../../Components/constantVariable'

let DeviceInfoForm = () => {
  let initialState = {
    i_imei_no: '',
    s_sim_no: '',
    s_sim_op: '',
    s_dvc_typ: '',
    dvc_mdl_name: '',
    dvc_timezone: '',
    dvc_mfg_dt: '',
    dvc_add_dt: '',
    dvc_dlt_dt: null,
    s_atd: '',
    s_dvc_status: '',
    is_ign_wr: false,
    is_air_wr: false
  }

  let [deviceInfo, setDeviceInfo] = useState({
    i_imei_no: '',
    s_sim_no: '',
    s_sim_op: '',
    s_dvc_typ: '',
    dvc_mdl_name: '',
    dvc_timezone: '',
    dvc_mfg_dt: '',
    dvc_add_dt: '',
    dvc_dlt_dt: null,
    s_atd: '',
    s_dvc_status: '',
    is_ign_wr: false,
    is_air_wr: false
  })


  let [InactiveSimList, setInactiveSimList] = useState({ data: [] })
  useEffect(() => {
    fetch(`${ubuntuIP}/api/v0/getInactiveSimDetails`)
      .then(response => response.json())
      .then(data => {
        setInactiveSimList({ data })
      })
      .catch(error => {
        console.error('Error: ', error)
      })
  }, [])


  let [showToggleValue, setShowToggleValue] = useState(false)
  let toggleInfo = () => {
    setShowToggleValue(prevShowToggleValue => {
      setDeviceInfo(prevData => ({
        ...prevData,
        is_ign_wr: !prevShowToggleValue
      }))
      return !prevShowToggleValue
    })
  }

  let [showIsAirCon, setShowIsAirCon] = useState(false)
  let toggleAirInfo = () => {
    setShowIsAirCon(prevShowToggleValue => {
      setDeviceInfo(prevData => ({
        ...prevData,
        is_air_wr: !prevShowToggleValue
      }))
      return !prevShowToggleValue
    })
  }

  let [deviceType, setDeviceType] = useState({ data: [] })
  useEffect(() => {
    fetch(`${ubuntuIP}/api/v0/getDeviceTypeDetails`)
      .then(response => response.json())
      .then(data => {
        setDeviceType({ data })
      })
      .catch(error => {
