import React, { useState, useEffect } from 'react'
import './deviceDetails.css'

import { ubuntuIP } from '../constantVariable'

let DeviceDeactivation = () => {
  let initialState = {
    i_imei_no: '',
    dvc_dlt_dt: null
  }

  let [deviceInfo, setDeviceInfo] = useState({
    i_imei_no: '',
    dvc_dlt_dt: null,
  })





  let [showToggleValue, setShowToggleValue] = useState(false)
  let toggleInfo = () => {
    setShowToggleValue(prevShowToggleValue => {
      setDeviceInfo(prevData => ({
        ...prevData,
        is_ign_wr: !prevShowToggleValue
      }))
      return !prevShowToggleValue
    })
  }

  let [showIsAirCon, setShowIsAirCon] = useState(false)
  let toggleAirInfo = () => {
    setShowIsAirCon(prevShowToggleValue => {
      setDeviceInfo(prevData => ({
        ...prevData,
        is_air_wr: !prevShowToggleValue
      }))
      return !prevShowToggleValue
    })
  }



  let resetForm = () => {
    setDeviceInfo(initialState)
  }

  let refreshPage = () => {
    window.location.reload()
  }

  // let handleChange = e => {
  //   let { name, value } = e.target
  //   setDeviceInfo(prevData => ({ ...prevData, [name]: value }))
  // }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDeviceInfo(prevState => ({
      ...prevState,
      [name]: value
    }));

    if (name === 's_sim_no') {
      const selectedSim = InactiveSimList.data.data.find(sim => sim.s_sim_no === value);
      if (selectedSim) {
        setDeviceInfo(prevState => ({
          ...prevState,
          s_sim_op: selectedSim.s_sim_op
        }));
      } else {
        // If the user selects 'Select', reset the SIM operator field
        setDeviceInfo(prevState => ({
          ...prevState,
          s_sim_op: ''
        }));
      }
    }
  };




  let handleSubmit = e => {
    e.preventDefault()
    fetch(`${ubuntuIP}/api/v0/setDeviceDetails`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(deviceInfo)
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success in Device Entry form:', data)
        alert('Device details saved successfully!')
      })
      .catch(error => {
        console.error('Error:', error)
        alert('Error! Please try again.')
      })
  }

  return (
    <div>
      <div className='wrapper'>
        <div className='container'>
          <h2>Device Deactivation</h2>
          <div className='DeviceInfoForm'>
            <form onSubmit={handleSubmit}>
              <div className='form-group'>
                <label
                  htmlFor='i_imei_no'
                  className={`required-label ${deviceInfo.i_imei_no ? 'required' : ''
                    }`}
                >
                  IMEI No.:
                </label>
                <input
                  type='number'
                  id='i_imei_no'
                  name='i_imei_no'
                  required
                  value={deviceInfo.i_imei_no}
                  onChange={handleChange}
                />
              </div>

              <div className='form-group'>
                <label
                  htmlFor='dvc_mfg_dt'
                  className={`required-label ${deviceInfo.dvc_mfg_dt ? 'required' : ''
                    }`}
                >
                  Manufacture Date:
                </label>
                <input
                  type='date'
                  id='dvc_mfg_dt'
                  name='dvc_mfg_dt'
                  value={deviceInfo.dvc_mfg_dt}
                  onChange={handleChange}
                />
              </div>
              <div className='form-group'>
                <label
                  htmlFor='dvc_add_dt'
                  className={`required-label ${deviceInfo.dvc_add_dt ? 'required' : ''
                    }`}
                >
                  Device Entry Date:
                </label>
                <input
                  type='date'
                  id='dvc_add_dt'
                  name='dvc_add_dt'
                  value={deviceInfo.dvc_add_dt}
                  onChange={handleChange}
                />
              </div>
              <div className='form-group'>
                <label htmlFor='dvc_dlt_dt'>Device Remove Date:</label>
                <input
                  type='date'
                  id='dvc_dlt_dt'
                  name='dvc_dlt_dt'
                  value={deviceInfo.dvc_dlt_dt}
                  onChange={handleChange}
                />
              </div>
              <div className='form-group'>
                <label htmlFor='s_atd'>Attendance:</label>
                <select
                  id='s_atd'
                  name='s_atd'
                  value={deviceInfo.s_atd}
                  onChange={handleChange}
                >
                  <option value=''>Select</option>
                  <option value='RF'>RFID</option>
                  <option value='IB'>IButton</option>
                </select>
              </div>
              <div className='form-group'>
                <label htmlFor='is_ign_wr'>Ignition Wire Connected:</label>
                <input
                  type='checkbox'
                  id='is_ign_wr'
                  name='is_ign_wr'
                  checked={showToggleValue}
                  onChange={toggleInfo}
                />
              </div>
              <div className='form-group'>
                <label htmlFor='is_air_wr'>Aircondition Wire Connected:</label>
                <input
                  type='checkbox'
                  id='is_air_wr'
                  name='is_air_wr'
                  checked={showIsAirCon}
                  onChange={toggleAirInfo}
                />
              </div>
              <div class='form-buttons'>
                <button type='submit'>Save</button>
                <button
                  type='button'
                  className='cancel-button'
                  onClick={() => {
                    resetForm()
                  }}
                >
                  Cancel
                </button>
                <button
                  type='button'
                  className='refresh-button'
                  onClick={refreshPage}
                >
                  Refresh
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default DeviceDeactivation
